import React, {Component} from 'react';
import MessageBoard from './MessageBoard.js'
import Home from './Home'
import UserPost from './UserPosts'
import Auth from '../modules/Auth.js'

class Nav extends Component{
  constructor(props){
    super(props)
    this.state = {
      currentVeiw:{
        board: false,
        posts: true,
        userPost:false,
        home:true
      }
    }
  }

  changeNav = (e) => {
    let obj = {}
    for (let key in this.state.currentVeiw) {
      obj[key] = false
    }
    obj[e.target.id] = true
    this.setState({
      currentVeiw: obj
    })
  }

  logout = () => {
    fetch('http:localhost:3000/logout',{
      method:'DELETE',
      headers:{
        token: Auth.getToken(),
        'Authorization': `Token ${Auth.getToken()}`
      }
    })
    .then((res) => {
      Auth.deauthenticateUser()
      this.props.logState()
    })
  }

  render(){
    return(
      <>
      <div className = "navBar" onClick={this.changeNav}>
        <img src = "../public/homepage.png" id = "posts"/>





        <h2 id="posts">Home </h2>
        <h2 id="board"> View Posts </h2>
        <h2 id="userPost">User</h2>
        <h2 id="home" onClick={this.logout}>Logout </h2>
      </div>
      {this.state.currentVeiw.board?
      <MessageBoard />:""}
      {this.state.currentVeiw.posts?
      <Home />:""}
      {this.state.currentVeiw.userPost?
      <UserPost />:""}

      </>
    )
  }
}

export default Nav
